    /** @brief Gauss-Lobatto nodes on [0,1] */
    template<int P>
    struct GaussLobatto
    {
        /** Nodal points */
        static const double nodes[P];
        /** Differentiation matrix */
        static const Mat<P,P> diff;
    };

    /** @brief The master element: [0,1]^N */
    template<int N, int P>
    struct Master
    {
        /** Order of polynomial */
        static const int p = P-1;
        /** Number of nodes per element */
        static const int npl = ipow(P,N);
        /** Mass matrix */
        static const KronMat<N,P> mass;
        /** Inverse mass matrix */
        static const KronMat<N,P> invmass;
        /** The index-th node in a given cell */
        static Tuple<double,N> dgnodes(Tuple<int,N> index, Cell<N> cell = Cell<N>())
        {
            assert((0 <= index).all() && (index < P).all());
            Tuple<double,N> node;
            for (int i=0; i<N; ++i) {
                node[i] = GaussLobatto<P>::nodes[index[i]];
            }
            return cell.lower + cell.width() * node;
        }
    };

    template<int P>
    struct LagrangePoly
    {
        /** The Lagrange polynomial denominators */
        static const double denom[P];
        /** Evaluate the P 1-D Lagrange polynomials at the point x */
        static Vec<P> eval(const double& x)
        {
            Vec<P> lagrange;
            // First compute the products to the left of the split
            double alpha = 1;
            lagrange[0] = denom[0];
            for (int i=0; i<P-1; ++i) {
                alpha *= x - GaussLobatto<P>::nodes[i];
                lagrange[i+1] = alpha * denom[i+1];
            }
            // Now compute the products to the right of the split
            double beta = 1;
            for (int i=P-1; i>0; --i) {
                beta *= x - GaussLobatto<P>::nodes[i];
                lagrange[i-1] *= beta;
            }
            return lagrange;
        }
        /** Evaluate the P^N N-D Lagrange polynomials at the coordinate x */
        template<int N>
        static KronVec<N,P> eval(const Tuple<double,N>& x)
        {
            // Precompute the P 1-D Lagrange polynomials evaluated at each
            // component of x
            Mat<P,N> g;
            for (int j=0; j<N; ++j) {
                g.col(j) = eval(x[j]);
            }
            // Tensor product the components together
            KronVec<N,P> lagrange;
            for (RangeIterator<N,P> it; it != Range<N,P>::end(); ++it) {
                int i = it.linearIndex();
                lagrange(i) = 1;
                for (int j=0; j<N; ++j) {
                    lagrange(i) *= g(it(j),j);
                }
            }
            return lagrange;
        }
    };

    /** @brief Quadrature nodes and weights on [0,1] */
    template<int Q>
    struct Quadrature
    {
        /** Quadrature points */
        static const double nodes[Q];
        /** Quadrature weights */
        static const double weights[Q];
    };

    /** @brief Compute a tensor product of matrices */
    template<int N, int P>
    KronMat<N,P> tensorProduct(const Mat<P>& A)
    {
        KronMat<N,P> M;
        for (RangeIterator<N,P> it; it != Range<N,P>::end(); ++it) {
            int i = it.linearIndex();
            for (RangeIterator<N,P> jt; jt != Range<N,P>::end(); ++jt) {
                int j = jt.linearIndex();
                M(i,j) = 1.0;
                for (int k=0; k<N; ++k) {
                    M(i,j) *= A(it(k),jt(k));
                }
            }
        }
        return M;
    }

